import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, ScrollView, ActivityIndicator } from 'react-native';
import { useSelector, useDispatch } from 'react-redux';
import { setDailyCard } from '../../store/slices/tarotSlice';
import { getDailyCard, saveDailyCard } from '../../services/supabase/supabaseClient';
import TarotCard3D from '../../components/3d/TarotCard3D';
import TarotCardFallback from '../../components/cards/TarotCardFallback';
import Button from '../../components/common/Button';
import Input from '../../components/common/Input';
import AudioReactiveComponent from '../../components/animations/AudioReactiveComponent';

const DailyCardScreen = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [reflection, setReflection] = useState('');
  const [isFlipped, setIsFlipped] = useState(false);
  const [showReflection, setShowReflection] = useState(false);
  const [audioEnabled, setAudioEnabled] = useState(false);
  const [use3D, setUse3D] = useState(true);

  const dispatch = useDispatch();
  const dailyCard = useSelector(state => state.tarot.dailyCard);
  const user = useSelector(state => state.auth.user);

  // Sample card data for demo
  const sampleCard = {
    id: '1',
    name: "The Fool",
    number: "0",
    arcana: "Major Arcana",
    suit: null,
    image: "m00.jpg",
    fortune_telling: [
      "Watch for new projects and new beginnings",
      "Prepare to take something on faith",
      "Something new comes your way; go for it"
    ],
    keywords: ["freedom", "faith", "inexperience", "innocence"],
    meanings: {
      light: [
        "Freeing yourself from limitation",
        "Expressing joy and youthful vigor",
        "Being open-minded",
        "Taking a leap of faith"
      ],
      shadow: [
        "Being gullible and naive",
        "Taking unnecessary risks",
        "Failing to be serious when required"
      ]
    },
    modern_interpretation: "This card represents freeing yourself from limitation, expressing joy, and being open-minded.",
    affirmation: "I embrace freedom and welcome its energy into my life."
  };

  useEffect(() => {
    fetchDailyCard();
  }, []);

  const fetchDailyCard = async () => {
    setLoading(true);
    setError('');

    try {
      if (user && user.id !== 'demo-user') {
        // In a real app, we would fetch from Supabase
        const { data, error } = await getDailyCard(user.id, new Date());

        if (error) {
          throw error;
        }

        if (data && data.cards) {
          dispatch(setDailyCard(data.cards));
        } else {
          // Generate a new daily card if none exists
          await generateNewDailyCard();
        }
      } else {
        // For demo purposes, use sample data
        dispatch(setDailyCard(sampleCard));
        setTimeout(() => setLoading(false), 1000); // Simulate loading
      }
    } catch (err) {
      console.error('Error fetching daily card:', err);
      setError('Failed to load your daily card. Please try again.');
      setLoading(false);
    }
  };

  const generateNewDailyCard = async () => {
    // In a real app, we would randomly select a card and save to Supabase
    try {
      if (user && user.id !== 'demo-user') {
        const randomCardId = '1'; // In a real app, this would be random
        const { error } = await saveDailyCard(user.id, randomCardId, new Date());

        if (error) {
          throw error;
        }

        dispatch(setDailyCard(sampleCard));
      } else {
        dispatch(setDailyCard(sampleCard));
      }
    } catch (err) {
      console.error('Error generating daily card:', err);
      setError('Failed to generate your daily card. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleFlip = (flipped) => {
    setIsFlipped(flipped);
  };

  const handleSaveReflection = async () => {
    if (!reflection.trim()) return;

    try {
      if (user && user.id !== 'demo-user') {
        // In a real app, we would save to Supabase
        // const { error } = await updateDailyCardReflection(user.id, new Date(), reflection);

        // if (error) {
        //   throw error;
        // }
      }

      setShowReflection(true);
    } catch (err) {
      console.error('Error saving reflection:', err);
      setError('Failed to save your reflection. Please try again.');
    }
  };

  const toggleAudio = () => {
    setAudioEnabled(!audioEnabled);
  };

  const toggle3D = () => {
    setUse3D(!use3D);
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#9B59B6" />
        <Text style={styles.loadingText}>Drawing your card of the day...</Text>
      </View>
    );
  }

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.title}>Your Card of the Day</Text>
      <Text style={styles.date}>{new Date().toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</Text>

      <View style={styles.cardContainer}>
        {audioEnabled ? (
          <AudioReactiveComponent 
            audioFile="../../assets/audio/effects/magical_chime.mp3"
            sensitivity={2}
            autoPlay={true}
            loop={true}
            visualizerType="pulse"
            style={styles.audioContainer}
          >
            {use3D ? (
              <TarotCard3D 
                card={dailyCard}
                onFlip={handleFlip}
                initiallyFlipped={isFlipped}
              />
            ) : (
              <TarotCardFallback 
                card={dailyCard}
                onFlip={handleFlip}
                initiallyFlipped={isFlipped}
              />
            )}
          </AudioReactiveComponent>
        ) : (
          use3D ? (
            <TarotCard3D 
              card={dailyCard}
              onFlip={handleFlip}
              initiallyFlipped={isFlipped}
            />
          ) : (
            <TarotCardFallback 
              card={dailyCard}
              onFlip={handleFlip}
              initiallyFlipped={isFlipped}
            />
          )
        )}
      </View>

      <View style={styles.infoContainer}>
        <Text style={styles.cardName}>{dailyCard?.name}</Text>
        <Text style={styles.cardDescription}>{dailyCard?.modern_interpretation}</Text>

        {dailyCard?.affirmation && (
          <View style={styles.affirmationContainer}>
            <Text style={styles.affirmationTitle}>Today's Affirmation:</Text>
            <Text style={styles.affirmation}>{dailyCard.affirmation}</Text>
          </View>
        )}

        {dailyCard?.fortune_telling && dailyCard.fortune_telling.length > 0 && (
          <View style={styles.fortuneContainer}>
            <Text style={styles.fortuneTitle}>Fortune Telling:</Text>
            {dailyCard.fortune_telling.map((fortune, index) => (
              <Text key={index} style={styles.fortuneItem}>â€¢ {fortune}</Text>
            ))}
          </View>
        )}
      </View>

      <View style={styles.reflectionContainer}>
        <Text style={styles.reflectionTitle}>Your Reflection</Text>
        {showReflection ? (
          <View style={styles.savedReflection}>
            <Text style={styles.reflectionText}>{reflection}</Text>
            <Button 
              title="Edit" 
              onPress={() => setShowReflection(false)} 
              type="outline"
              size="small"
            />
          </View>
        ) : (
          <>
            <Input
              value={reflection}
              onChangeText={setReflection}
              placeholder="Write your thoughts about today's card..."
              multiline
              numberOfLines={4}
            />
            <Button 
              title="Save Reflection" 
              onPress={handleSaveReflection} 
              type="secondary"
            />
          </>
        )}
      </View>

      <View style={styles.optionsContainer}>
        <Button 
          title={audioEnabled ? "Disable Audio" : "Enable Audio"} 
          onPress={toggleAudio} 
          type="outline"
          size="small"
          style={styles.optionButton}
        />
        <Button 
          title={use3D ? "Use 2D Card" : "Use 3D Card"} 
          onPress={toggle3D} 
          type="outline"
          size="small"
          style={styles.optionButton}
        />
      </View>

      {error ? <Text style={styles.errorText}>{error}</Text> : null}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flexGrow: 1,
    backgroundColor: '#FAF3E0',
    padding: 20,
    alignItems: 'center',
  },
  loadingContainer: {
    flex: 1,
    backgroundColor: '#FAF3E0',
    justifyContent: 'center',
    alignItems: 'center',
  },
  loadingText: {
    marginTop: 20,
    fontSize: 18,
    color: '#9B59B6',
    fontStyle: 'italic',
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: '#9B59B6',
    marginTop: 40,
    marginBottom: 5,
  },
  date: {
    fontSize: 16,
    color: '#8E44AD',
    marginBottom: 30,
  },
  cardContainer: {
    height: 400,
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: 30,
  },
  audioContainer: {
    borderRadius: 20,
    padding: 20,
  },
  infoContainer: {
    width: '100%',
    marginBottom: 30,
  },
  cardName: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#9B59B6',
    textAlign: 'center',
    marginBottom: 10,
  },
  cardDescription: {
    fontSize: 16,
    color: '#4A4A4A',
    textAlign: 'center',
    marginBottom: 20,
    lineHeight: 24,
  },
  affirmationContainer: {
