    lineHeight: 22,
  },
  reflectionContainer: {
    width: '100%',
    marginBottom: 30,
  },
  reflectionTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#9B59B6',
    marginBottom: 15,
  },
  savedReflection: {
    backgroundColor: '#FFFFFF',
    padding: 15,
    borderRadius: 10,
    marginBottom: 10,
  },
  reflectionText: {
    fontSize: 16,
    color: '#4A4A4A',
    marginBottom: 10,
    lineHeight: 22,
  },
  optionsContainer: {
    flexDirection: 'row',
    justifyContent: 'center',
    marginBottom: 30,
  },
  optionButton: {
    marginHorizontal: 5,
  },
  errorText: {
    color: '#E74C3C',
    marginBottom: 20,
    textAlign: 'center',
  },
});

export default DailyCardScreen;
