import React, { useState } from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity, ScrollView } from 'react-native';
import Input from '../../components/common/Input';
import Button from '../../components/common/Button';
import { signIn } from '../../services/supabase/supabaseClient';
import { useDispatch } from 'react-redux';
import { setUser, setSession } from '../../store/slices/authSlice';

const LoginScreen = ({ navigation }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const dispatch = useDispatch();

  const handleLogin = async () => {
    if (!email || !password) {
      setError('Please enter both email and password');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const { data, error } = await signIn(email, password);

      if (error) {
        setError(error.message);
      } else if (data) {
        dispatch(setUser(data.user));
        dispatch(setSession(data.session));
      }
    } catch (err) {
      setError('An unexpected error occurred');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = () => {
    navigation.navigate('Register');
  };

  // For demo purposes, we'll add a demo login option
  const handleDemoLogin = () => {
    dispatch(setUser({ id: 'demo-user', email: 'demo@example.com' }));
    dispatch(setSession({ token: 'demo-token' }));
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <View style={styles.logoContainer}>
        <Text style={styles.logoText}>Tarot</Text>
        <Text style={styles.tagline}>Discover your path</Text>
      </View>

      <View style={styles.formContainer}>
        <Input
          label="Email"
          value={email}
          onChangeText={setEmail}
          placeholder="Enter your email"
        />

        <Input
          label="Password"
          value={password}
          onChangeText={setPassword}
          placeholder="Enter your password"
          secureTextEntry
        />

        {error ? <Text style={styles.errorText}>{error}</Text> : null}

        <Button
          title={loading ? "Logging in..." : "Login"}
          onPress={handleLogin}
          disabled={loading}
          style={styles.loginButton}
        />

        <TouchableOpacity onPress={() => {}}>
          <Text style={styles.forgotPassword}>Forgot Password?</Text>
        </TouchableOpacity>

        <View style={styles.divider}>
          <View style={styles.dividerLine} />
          <Text style={styles.dividerText}>or</Text>
          <View style={styles.dividerLine} />
        </View>

        <Button
          title="Create an Account"
          onPress={handleRegister}
          type="outline"
        />

        <Button
          title="Try Demo"
          onPress={handleDemoLogin}
          type="secondary"
          style={styles.demoButton}
        />
      </View>

      <View style={styles.footer}>
        <Text style={styles.footerText}>
          By continuing, you agree to our Terms of Service and Privacy Policy
        </Text>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flexGrow: 1,
    backgroundColor: '#FAF3E0',
    padding: 20,
    justifyContent: 'center',
  },
  logoContainer: {
    alignItems: 'center',
    marginBottom: 40,
  },
  logoText: {
    fontSize: 42,
    fontWeight: 'bold',
    color: '#9B59B6',
    marginBottom: 10,
  },
  tagline: {
    fontSize: 18,
    color: '#8E44AD',
    fontStyle: 'italic',
  },
  formContainer: {
    width: '100%',
    marginBottom: 30,
  },
  loginButton: {
    marginTop: 10,
  },
  forgotPassword: {
    color: '#9B59B6',
    textAlign: 'center',
    marginTop: 15,
    marginBottom: 15,
  },
  divider: {
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 20,
  },
  dividerLine: {
    flex: 1,
    height: 1,
    backgroundColor: '#E0D4C0',
  },
  dividerText: {
    marginHorizontal: 10,
    color: '#8E8E93',
  },
  demoButton: {
    marginTop: 10,
  },
  errorText: {
    color: '#E74C3C',
    marginBottom: 10,
    textAlign: 'center',
  },
  footer: {
    marginTop: 20,
  },
  footerText: {
    textAlign: 'center',
    color: '#8E8E93',
    fontSize: 12,
  },
});

export default LoginScreen;
