import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, FlatList, TouchableOpacity } from 'react-native';
import { useSelector, useDispatch } from 'react-redux';
import { setSpreads } from '../../store/slices/tarotSlice';
import { getSpreads } from '../../services/supabase/supabaseClient';
import SpreadCard from '../../components/spreads/SpreadCard';
import Button from '../../components/common/Button';

const SpreadsScreen = ({ navigation }) => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState('all'); // 'all', 'basic', 'advanced'

  const dispatch = useDispatch();
  const spreads = useSelector(state => state.tarot.spreads);
  const isSubscribed = useSelector(state => state.auth.isSubscribed);

  // Sample spreads data for demo
  const sampleSpreads = [
    {
      id: '1',
      name: 'Three Card Spread',
      description: 'A simple spread for past, present, and future insights.',
      is_premium: false,
      layout: {
        positions: [
          { id: 1, name: 'Past', x: 0, y: 0 },
          { id: 2, name: 'Present', x: 1, y: 0 },
          { id: 3, name: 'Future', x: 2, y: 0 }
        ]
      }
    },
    {
      id: '2',
      name: 'Celtic Cross',
      description: 'A comprehensive 10-card spread for deep insights into complex situations.',
      is_premium: true,
      layout: {
        positions: [
          { id: 1, name: 'Present', x: 1, y: 1 },
          { id: 2, name: 'Challenge', x: 1, y: 1, overlay: true },
          { id: 3, name: 'Foundation', x: 1, y: 2 },
          { id: 4, name: 'Past', x: 0, y: 1 },
          { id: 5, name: 'Crown', x: 1, y: 0 },
          { id: 6, name: 'Future', x: 2, y: 1 },
          { id: 7, name: 'Self', x: 3, y: 3 },
          { id: 8, name: 'Environment', x: 3, y: 2 },
          { id: 9, name: 'Hopes/Fears', x: 3, y: 1 },
          { id: 10, name: 'Outcome', x: 3, y: 0 }
        ]
      }
    },
    {
      id: '3',
      name: 'Relationship Spread',
      description: 'A 5-card spread focused on relationship dynamics and potential.',
      is_premium: false,
      layout: {
        positions: [
          { id: 1, name: 'You', x: 0, y: 0 },
          { id: 2, name: 'Partner', x: 2, y: 0 },
          { id: 3, name: 'Relationship', x: 1, y: 1 },
          { id: 4, name: 'Challenge', x: 0, y: 2 },
          { id: 5, name: 'Outcome', x: 2, y: 2 }
        ]
      }
    },
    {
      id: '4',
      name: 'Career Path',
      description: 'A specialized spread for career guidance and professional development.',
      is_premium: true,
      layout: {
        positions: [
          { id: 1, name: 'Current Position', x: 1, y: 0 },
          { id: 2, name: 'Challenges', x: 0, y: 1 },
          { id: 3, name: 'Opportunities', x: 2, y: 1 },
          { id: 4, name: 'Action', x: 1, y: 2 },
          { id: 5, name: 'Outcome', x: 1, y: 3 }
        ]
      }
    },
    {
      id: '5',
      name: 'Single Card Draw',
      description: 'A simple one-card reading for quick guidance.',
      is_premium: false,
      layout: {
        positions: [
          { id: 1, name: 'Guidance', x: 0, y: 0 }
        ]
      }
    }
  ];

  useEffect(() => {
    fetchSpreads();
  }, []);

  const fetchSpreads = async () => {
    setLoading(true);
    setError('');

    try {
      // In a real app, we would fetch from Supabase
      // const { data, error } = await getSpreads(isSubscribed);

      // if (error) {
      //   throw error;
      // }

      // For demo purposes, use sample data
      dispatch(setSpreads(sampleSpreads));
      setTimeout(() => setLoading(false), 500); // Simulate loading
    } catch (err) {
      console.error('Error fetching spreads:', err);
      setError('Failed to load spreads. Please try again.');
      setLoading(false);
    }
  };

  const handleSpreadSelect = (spread) => {
    if (spread.is_premium && !isSubscribed) {
      // Show upgrade prompt
      navigation.navigate('UpgradePrompt', { returnTo: 'Spreads' });
    } else {
      navigation.navigate('SpreadDetail', { spreadId: spread.id });
    }
  };

  const getFilteredSpreads = () => {
    if (filter === 'all') {
      return spreads;
    } else if (filter === 'basic') {
      return spreads.filter(spread => !spread.is_premium);
    } else if (filter === 'advanced') {
      return spreads.filter(spread => spread.is_premium);
    }
    return spreads;
  };

  const renderSpreadItem = ({ item }) => (
    <SpreadCard
      spread={item}
      onPress={() => handleSpreadSelect(item)}
      style={styles.spreadCard}
    />
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Tarot Spreads</Text>

      <View style={styles.filterContainer}>
        <TouchableOpacity
          style={[styles.filterButton, filter === 'all' && styles.filterButtonActive]}
          onPress={() => setFilter('all')}
        >
          <Text style={[styles.filterText, filter === 'all' && styles.filterTextActive]}>All</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.filterButton, filter === 'basic' && styles.filterButtonActive]}
          onPress={() => setFilter('basic')}
        >
          <Text style={[styles.filterText, filter === 'basic' && styles.filterTextActive]}>Basic</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.filterButton, filter === 'advanced' && styles.filterButtonActive]}
          onPress={() => setFilter('advanced')}
        >
          <Text style={[styles.filterText, filter === 'advanced' && styles.filterTextActive]}>Advanced</Text>
        </TouchableOpacity>
      </View>

      {loading ? (
        <View style={styles.loadingContainer}>
          <Text style={styles.loadingText}>Loading spreads...</Text>
        </View>
      ) : error ? (
        <View style={styles.errorContainer}>
          <Text style={styles.errorText}>{error}</Text>
          <Button title="Try Again" onPress={fetchSpreads} type="secondary" />
        </View>
      ) : (
        <FlatList
          data={getFilteredSpreads()}
          renderItem={renderSpreadItem}
          keyExtractor={item => item.id}
          contentContainerStyle={styles.spreadsList}
          showsVerticalScrollIndicator={false}
          ListEmptyComponent={
            <View style={styles.emptyContainer}>
              <Text style={styles.emptyText}>No spreads found</Text>
            </View>
          }
        />
      )}

      {!isSubscribed && (
        <View style={styles.upgradeContainer}>
          <Text style={styles.upgradeText}>Unlock all premium spreads</Text>
          <Button 
            title="Upgrade to Premium" 
            onPress={() => navigation.navigate('UpgradePrompt', { returnTo: 'Spreads' })}
            type="primary"
          />
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FAF3E0',
    padding: 20,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: '#9B59B6',
    marginTop: 40,
    marginBottom: 20,
  },
  filterContainer: {
    flexDirection: 'row',
    marginBottom: 20,
  },
  filterButton: {
    paddingVertical: 8,
    paddingHorizontal: 16,
    borderRadius: 20,
    marginRight: 10,
    backgroundColor: '#F0E6D2',
  },
  filterButtonActive: {
    backgroundColor: '#9B59B6',
  },
  filterText: {
    fontSize: 14,
    fontWeight: '500',
    color: '#9B59B6',
  },
  filterTextActive: {
    color: '#FFFFFF',
  },
  spreadsList: {
    paddingBottom: 100,
  },
  spreadCard: {
    marginBottom: 15,
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  loadingText: {
    fontSize: 16,
    color: '#9B59B6',
    fontStyle: 'italic',
  },
  errorContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  errorText: {
    fontSize: 16,
    color: '#E74C3C',
    textAlign: 'center',
    marginBottom: 20,
  },
  emptyContainer: {
    padding: 20,
    alignItems: 'center',
  },
  emptyText: {
    fontSize: 16,
    color: '#8E8E93',
    fontStyle: 'italic',
  },
  upgradeContainer: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    backgroundColor: '#FFFFFF',
    padding: 20,
    borderTopWidth: 1,
    borderTopColor: '#E0D4C0',
    alignItems: 'center',
  },
  upgradeText: {
    fontSize: 16,
    color: '#9B59B6',
    marginBottom: 10,
  },
});

export default SpreadsScreen;
